  '/mine',
   isAuth,
    expressAsyncHandler(async (req, res)=>{
     const orders = await Order.find({user: req.user._id });
     res.send(orders)
})
);
orderRouter.get(
  '/:id',
  isAuth,
  expressAsyncHandler(async (req, res)=>{
  const order = await Order.findById(req.params.id);
  if (order) {
    res.send(order);
  } else {
    res.status(404).send({message: 'Order Not Found'});
  }
 })
);

