    try {
        const user = new User({
          name: 'admin',
          email: 'admin@example.com',
          password: 'jsamazona',
          isAdmin: true,
        });
        const createdUser = await user.save();
        res.send(createdUser);
    } catch(err){
        res.status(500).send({ message: err.message });
    }
})
);
userRouter.post(
    '/signin',
 expressAsyncHandler( async (req, res) =>{
